import React from "react"
import styled from "styled-components"

const Portfolio = ({userInfos}) => {
	let tmp = userInfos.account;
	let res = tmp.substring(0, 5) + "..." + tmp.substring((tmp.length)-4);
	return (
		<Container>
			<img src={require("../pngaaa.com-4112190.png").default} style={{width:"15%"}}/>
			<TitleText>Ethereum</TitleText>
			<Currency>
				Polygon
			</Currency>
			<Money>{parseFloat(userInfos.balance).toFixed(3)} MATIC</Money>
			<Address>{res}</Address>
		</Container>
	)
}

const TitleText = styled.div`
	padding: 0.5rem;
	border-radius: 0.5rem;
	width: 50%;
	text-align: center;
`

const Container = styled.div`
	background-color: #191b1f;
	border : 1px solid #191b1f;
	padding: 0.5rem;
	border-radius: 0.5rem;
	display: flex;
	width: 40%;
	justify-content: space-between;
`

const Currency = styled.div`
	background-color: rgb(33, 36, 41);
	border: 1px solid rgb(33, 36, 41);
	color: rgb(149, 103, 255);
	padding: 0.5rem;
	border-radius: 0.5rem;
	font-family: Inter,sans-serif;
`

const Money = styled.div`
	background-color: rgb(33, 36, 41);
	border : 1px solid rgb(33, 36, 41);
	color: white;
	padding: 0.5rem;
	border-radius: 0.5rem;
	font-family: Inter,sans-serif;
`

const Address = styled.div`
	background-color: rgb(33, 36, 41);
	border : 1px solid rgb(33, 36, 41);
	color: white;
	padding: 0.5rem;
	border-radius: 0.5rem;
`

export default Portfolio
